import React from 'react';
import axios from 'axios';
import Typography from '@material-ui/core/Typography';

class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            ipAddress: null,
	    country: null,
	    region: null,
	    city: null,
	    isp: null,
	    as: null	
        };
    }

    componentDidMount() {
        // Simple GET request using axios
        //axios.get('https://api.ipify.org?format=json')
	axios.get('https://ipapi.co/114.5.212.244/json')
            .then(response => this.setState({ ipAddress: response.data.ip, country: response.data.country_name }));
    }

    render() {
        const { ipAddress, country } = this.state;
        return (
        	<><Typography variant="overline" display="block" gutterBottom>IP Address : {ipAddress}</Typography>
                <Typography variant="overline" display="block" gutterBottom>Country : {country}</Typography>
                <Typography variant="overline" display="block" gutterBottom>Region : </Typography>
                <Typography variant="overline" display="block" gutterBottom>City : </Typography>
                <Typography variant="overline" display="block" gutterBottom>ISP : </Typography>
		<Typography variant="overline" display="block" gutterBottom>AS : </Typography></>
	);
    }
}
export default App;
