import React from 'react';
import axios from 'axios';
import Typography from '@material-ui/core/Typography';

class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            ipAddress: null,
	    country: null,
	    region: null,
	    city: null,
	    org: null,
	    asn: null,
	    version: null,
		chargingStateEl: null,
		chargingTimeEl: null,
		dichargeTimeEl: null,
		levelEl: null
        };
    }

    componentDidMount() {
        // Simple GET request using axios
        //axios.get('https://api.ipify.org?format=json')
	axios.get('https://ipapi.co/json')
            .then(response => this.setState({ version: response.data.version, ipAddress: response.data.ip, country: response.data.country_name, region: response.data.region, city: response.data.city, org: response.data.org, asn: response.data.asn }));

	if ('getBattery' in navigator)
        {
                navigator.getBattery().then(function(battery) {
			this.setState({levelEl: (battery.level * 100)});
                });
        }
        else
        {
                this.setState({levelEl: <>Not Supported</>});
        }

    }


	UpdateBatteryUI(battery) {
  this.setState({levelEl: (battery.level * 100)});
  this.setState({chargingTimeEl: battery.chargingTime});
  this.setState({dichargeTimeEl: battery.dischargingTime});

  if (battery.charging === true) {
    this.setState({chargingStateEl: 'Charging'});
  } else if (battery.charging === false) {
    this.setState({chargingStateEl: 'Discharging'});
  }
}

	MonitorBattery(battery) {
  // Update the initial UI.
  this.UpdateBatteryUI(battery);


  // Monitor for futher updates.
  battery.addEventListener('levelchange',
    this.UpdateBatteryUI.bind(null, battery));
  battery.addEventListener('chargingchange',
    this.UpdateBatteryUI.bind(null, battery));
  battery.addEventListener('dischargingtimechange',
    this.UpdateBatteryUI.bind(null, battery));
  battery.addEventListener('chargingtimechange',
    this.UpdateBatteryUI.bind(null, battery));
	this.setState({levelEl: 'Not Supported 2'});
	}

    render() {
        const { ipAddress, country, region, city, asn, org, version, levelEl, chargingTimeE1, dischargingTimeE1, chargingStateE1 } = this.state;

	let batteryLevel = "";

        return (
        	<><Typography variant="overline" display="block" gutterBottom>IP Address : {ipAddress} ({version})</Typography>
                <Typography variant="overline" display="block" gutterBottom>Country : {country}</Typography>
                <Typography variant="overline" display="block" gutterBottom>Region : {region}</Typography>
                <Typography variant="overline" display="block" gutterBottom>City : {city}</Typography>
                <Typography variant="overline" display="block" gutterBottom>ISP : {org}</Typography>
		<Typography variant="overline" display="block" gutterBottom>ASystem Number : {asn}</Typography>
		<Typography variant="overline" display="block" gutterBottom>Battery Level : {levelEl}</Typography></>
	);
    }
}
export default App;
