import React from 'react';
import axios from 'axios';
import Typography from '@material-ui/core/Typography';

class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            ipAddress: null,
	    country: null,
	    region: null,
	    city: null,
	    org: null,
	    asn: null,
	    version: null	
        };
    }

    componentDidMount() {
        // Simple GET request using axios
        //axios.get('https://api.ipify.org?format=json')
	axios.get('https://ipapi.co/json')
            .then(response => this.setState({ version: response.data.version, ipAddress: response.data.ip, country: response.data.country_name, region: response.data.region, city: response.data.city, org: response.data.org, asn: response.data.asn }));
    }




    render() {
        const { ipAddress, country, region, city, asn, org, version } = this.state;

let batteryIsCharging = false;

navigator.getBattery().then(function(battery) {
  batteryIsCharging = battery.charging;

  battery.addEventListener('chargingchange', function() {
    batteryIsCharging = battery.charging;
  });
});

        return (
        	<><Typography variant="overline" display="block" gutterBottom>IP Address : {ipAddress} ({version})</Typography>
                <Typography variant="overline" display="block" gutterBottom>Country : {country}</Typography>
                <Typography variant="overline" display="block" gutterBottom>Region : {region}</Typography>
                <Typography variant="overline" display="block" gutterBottom>City : {city}</Typography>
                <Typography variant="overline" display="block" gutterBottom>ISP : {org}</Typography>
		<Typography variant="overline" display="block" gutterBottom>Automatic System Number : {asn}</Typography></>
	);
    }
}
export default App;
