import {isMobileOnly, isTablet, browserVersion, browserName,  mobileModel, osName, osVersion} from  'react-device-detect';
import React, { useEffect, Fragment, Component } from 'react';
import ReactDOM from 'react-dom';
import { makeStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import FaceTwoToneIcon from '@material-ui/icons/FaceTwoTone';
import Paper from '@material-ui/core/Paper';
import MediaQuery from 'react-responsive';
import DashboardTwoToneIcon from '@material-ui/icons/DashboardTwoTone';
import FilterDramaTwoToneIcon from '@material-ui/icons/FilterDramaTwoTone';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import App from './App';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';

const useStyles = makeStyles((theme) => ({
   card: {
    maxWidth: 345,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: '#ff0000',
  },
  icon: {
    '& svg': {
      fontSize: 20
    },
    '&:hover': {
      backgroundColor: 'transparent'
    }
  },
  menu: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'flex-end',
    '& > *': {
      margin: theme.spacing(0),
      height: theme.spacing(5),
    }
  }
}));

function ClientInfo()
{

	let custInfo;
	const ipAddress = <App />;
	if(isMobileOnly)
	{
		custInfo = <div> {ipAddress} from : {osName} {osVersion} Mobile : {mobileModel} using : {browserName} {browserVersion}</div>
	}
	else if(isTablet)
	{
		custInfo = <div> from : {osName} {osVersion} Tablet : {mobileModel} using : {browserName} {browserVersion}</div>
	}
	else
	{
		custInfo = <div> {ipAddress} from : {osName} {osVersion} Device using : {browserName} {browserVersion}</div>
	}
	return custInfo;
}

function CreateCard()
{
const classes = useStyles();
const [expanded, setExpanded] = React.useState(false);
return
(
	<Card className={classes.card}>
      <CardHeader
        avatar={
          <Avatar aria-label="recipe" className={classes.avatar}>
            R
          </Avatar>
        }
        action={
          <IconButton aria-label="settings">
            <MoreVertIcon />
          </IconButton>
        }
        title="Shrimp and Chorizo Paella"
        subheader="September 14, 2016"
      />
      <CardMedia
        className={classes.media}
        image="/static/images/cards/paella.jpg"
        title="Paella dish"
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          This impressive paella is a perfect party dish and a fun meal to cook together with your
          guests. Add 1 cup of frozen peas along with the mussels, if you like.
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
        <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded,
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography paragraph>Method:</Typography>
          <Typography paragraph>
            Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
            minutes.
          </Typography>
          <Typography paragraph>
            Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over medium-high
            heat. Add chicken, shrimp and chorizo, and cook, stirring occasionally until lightly
            browned, 6 to 8 minutes. Transfer shrimp to a large plate and set aside, leaving chicken
            and chorizo in the pan. Add pimentón, bay leaves, garlic, tomatoes, onion, salt and
            pepper, and cook, stirring often until thickened and fragrant, about 10 minutes. Add
            saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
          </Typography>
          <Typography paragraph>
            Add rice and stir very gently to distribute. Top with artichokes and peppers, and cook
            without stirring, until most of the liquid is absorbed, 15 to 18 minutes. Reduce heat to
            medium-low, add reserved shrimp and mussels, tucking them down into the rice, and cook
            again without stirring, until mussels have opened and rice is just tender, 5 to 7
            minutes more. (Discard any mussels that don’t open.)
          </Typography>
          <Typography>
            Set aside off of the heat to let rest for 10 minutes, and then serve.
          </Typography>
        </CardContent>
      </Collapse>
    </Card>
);
}

function SvgIconsSize() {
const classes = useStyles();
const custInfo = ClientInfo();
  return (
	<React.Fragment>
                <MediaQuery minDeviceWidth={1224}>
			<div></div>
			<div className={classes.menu}>
			<Paper elevation={0} style={{display: 'flex', flexGrow: '1', justifyContent:'flex-end', backgroundColor: '#f4f9fd'}}><div>&nbsp;</div></Paper>
                        <Paper elevation={2} style={{display: 'flex', justifyContent:'flex-end', backgroundColor: '#e5f1fb'}}>
                                <IconButton className={classes.icon}>
                                        &nbsp;
					<DashboardTwoToneIcon style={{color:'#0275d8'}}/>&nbsp;<strong><span style={{color:'#0275d8', fontSize:'18px'}}>Service</span></strong>
				</IconButton>
			</Paper>&nbsp;
			<Paper elevation={2} style={{display: 'flex', justifyContent:'flex-end', backgroundColor: '#eef7ee'}}>
				<IconButton className={classes.icon}>
                                        &nbsp;
                                        <FilterDramaTwoToneIcon style={{color:'#5cb85c'}}/>&nbsp;<strong><span style={{color:'#5cb85c', fontSize:'18px'}}>Product</span></strong>
                                </IconButton>
                        </Paper>&nbsp;
                        <Paper elevation={2} style={{display: 'flex', justifyContent:'flex-end', backgroundColor: '#fdf6ed'}}>
                                <IconButton className={classes.icon}>
					&nbsp;
                                        <FaceTwoToneIcon style={{color:'#f0ad4e'}}/>&nbsp;<strong><span style={{color:'#f0ad4e', fontSize:'18px'}}>About Us</span></strong>&nbsp;
                                </IconButton>
                        </Paper>&nbsp;
                        </div>
                </MediaQuery>
                <MediaQuery maxDeviceWidth={1224}>
		<div></div>
                </MediaQuery>
                <MediaQuery orientation='portrait'>
		<div></div>
                </MediaQuery>
                <MediaQuery orientation='landscape'>
		<div></div>
                </MediaQuery>

                <MediaQuery minResolution='2dppx'>
		<div></div>
                </MediaQuery>
<CssBaseline />
      <Container maxWidth="lg">
	{custInfo}
      </Container>
            </React.Fragment>
	);
}

ReactDOM.render(
  // Try changing to isLoggedIn={true}:
  <SvgIconsSize />,
  document.getElementById('root')
);
